/**
 * Retrieves a font by its name.
 * @param name The name of the font to retrieve.
 * @return The font corresponding to the name.
 */
PalmyraOS::Font& PalmyraOS::FontManager::getFont(const char* name) {
    if (strcmp(name, "Arial-12") == 0) return Font::Arial12;
    if (strcmp(name, "FiraCode-Light-12") == 0) return Font::FiraCodeLight12;
    return Font::Arial12;
}

/**
 * Initializes the font manager and loads default fonts.
 */
void PalmyraOS::FontManager::initialize() { Font::initializeFonts(); }